import React from "react";
import ReactDOM  from "react-dom";

import "./index.css";

class App extends React.Component{

  heightInput = React.createRef();

  state = {
    title:"Duck : Wallpaper Creator",
    width:200,
    height:200,
    exampleStyle:{
      border: "black solid 2px",
      width:"200px",
      height:"200px",
      backgroundColor: "aqua"
    }
  }

  handleChange = () =>{
    this.setState({exampleStyle: {height:event.currentTarget.value+"px" }});
  }

  render(){
    return(
      <div>
        <h1>{this.state.title}</h1>

        <form onChange={this.handleChange}>
        <label>Largeur </label>
        <input type="number" value={"200"}></input>
        <br></br>
        <label>Hauteur </label>
        <input ref={this.heightInput} type="number" value={"200"}></input>
        </form>

        <div style={this.state.exampleStyle}></div>




      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
