import React from "react";
import ReactDOM  from "react-dom";

import "./index.css";

class App extends React.Component{

  heightInput = React.createRef();
  widthInput = React.createRef();

  state = {
    title:"Duck : Wallpaper Creator",
    width:300,
    height:200,
    exampleStyle:{
      border: "black solid 2px",
      width:`200px`,
      height:"200px",
      backgroundColor: "aqua",
      position: "absolute",
      left:"50%",

      transform:"translateX(-50%)"
    }
  }

  handleChange = () =>{
    let heightValue = this.heightInput.current.value;
    let widthValue = this.widthInput.current.value;

    let copy = {...this.state.exampleStyle};
    copy["height"] = heightValue.toString() + "px";
    copy["width"] = widthValue.toString() + "px";
    this.setState({height:heightValue, width:widthValue});
    this.setState({exampleStyle: copy });

  }

  render(){
    return(
      <div>
        <h1>{this.state.title}</h1>

        <form>
        <label>Largeur </label>
        <input onChange={this.handleChange} ref={this.widthInput} type="number" value={this.state.width}></input>
        <br></br>
        <label>Hauteur </label>
        <input  onChange={this.handleChange} ref={this.heightInput} type="number" value={this.state.height}></input>
        </form>

        <br></br>
        <div style={this.state.exampleStyle}></div>
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
